function initializeTheme(){const e=localStorage.getItem("theme"),t=window.matchMedia("(prefers-color-scheme: dark)").matches,n=e||(t?"dark":"light");document.documentElement.setAttribute("data-theme",n)}function toggleTheme(){document.documentElement.classList.add("theme-transition");const t=document.documentElement.getAttribute("data-theme"),e=t==="dark"?"light":"dark";document.documentElement.setAttribute("data-theme",e),localStorage.setItem("theme",e),setTimeout(()=>{document.documentElement.classList.remove("theme-transition")},150)}initializeTheme()